import React, { useContext, useEffect, useReducer, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import axios from 'axios';
import { Store } from '../Store';
import { getError } from '../utils';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import Form from 'react-bootstrap/Form';
import { Helmet } from 'react-helmet-async';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import Button from 'react-bootstrap/Button';

const reducer = (state, action) => {
	switch (action.type) {
		case 'FETCH_REQUEST':
			return { ...state, loading: true };
		case 'FETCH_SUCCESS':
			return { ...state, loading: false };
		case 'FETCH_FAIL':
			return { ...state, loading: false, error: action.payload };
		case 'UPDATE_REQUEST':
			return { ...state, loadingUpdate: true };
		case 'UPDATE_SUCCESS':
			return { ...state, loadingUpdate: false };
		case 'UPDATE_FAIL':
			return { ...state, loadingUpdate: false };

		default:
			return state;
	}
};
export default function EventEditScreen() {
	const navigate = useNavigate();
	const params = useParams(); // /event/:id
	const { id: eventId } = params;

	const { state } = useContext(Store);
	const { userInfo } = state;
	const [ { loading, error, loadingUpdate }, dispatch ] = useReducer(reducer, {
		loading: true,
		error: ''
	});

	const [ name, setName ] = useState('');
	const [ slug, setSlug ] = useState('');
	const [ price, setPrice ] = useState('');
	const [ image, setImage ] = useState('');
	const [ category, setCategory ] = useState('');
	const [ openSeats, setOpenSeats ] = useState('');
	const [ description, setDescription ] = useState('');

	useEffect(
		() => {
			const fetchData = async () => {
				try {
					dispatch({ type: 'FETCH_REQUEST' });
					const { data } = await axios.get(`/api/events/${eventId}`);
					setName(data.name);
					setSlug(data.slug);
					setPrice(data.price);
					setImage(data.image);
					setCategory(data.category);
					setOpenSeats(data.openSeats);
					setDescription(data.description);
					dispatch({ type: 'FETCH_SUCCESS' });
				} catch (err) {
					dispatch({
						type: 'FETCH_FAIL',
						payload: getError(err)
					});
				}
			};
			fetchData();
		},
		[ eventId ]
	);

	const submitHandler = async (e) => {
		e.preventDefault();
		try {
			dispatch({ type: 'UPDATE_REQUEST' });
			await axios.put(
				`/api/events/${eventId}`,
				{
					_id: eventId,
					name,
					slug,
					price,
					image,
					category,
					openSeats,
					description
				},
				{
					headers: { Authorization: `Bearer ${userInfo.token}` }
				}
			);
			dispatch({
				type: 'UPDATE_SUCCESS'
			});
			toast.success('Evenimentul a fost actualizat cu succes');
			navigate('/admin/events');
		} catch (err) {
			toast.error(getError(err));
			dispatch({ type: 'UPDATE_FAIL' });
		}
	};
	return (
		<Container className="small-container">
			<Helmet>
				<title>Modifica evenimentul ${eventId}</title>
			</Helmet>
			<h1>Editeaza Eventimentul {eventId}</h1>

			{loading ? (
				<LoadingBox />
			) : error ? (
				<MessageBox variant="danger">{error}</MessageBox>
			) : (
				<Form onSubmit={submitHandler}>
					<Form.Group className="mb-3" controlId="name">
						<Form.Label>Nume</Form.Label>
						<Form.Control value={name} onChange={(e) => setName(e.target.value)} required />
					</Form.Group>
					<Form.Group className="mb-3" controlId="slug">
						<Form.Label>URL</Form.Label>
						<Form.Control value={slug} onChange={(e) => setSlug(e.target.value)} required />
					</Form.Group>
					<Form.Group className="mb-3" controlId="name">
						<Form.Label>Pret</Form.Label>
						<Form.Control value={price} onChange={(e) => setPrice(e.target.value)} required />
					</Form.Group>
					<Form.Group className="mb-3" controlId="image">
						<Form.Label>Imagine</Form.Label>
						<Form.Control value={image} onChange={(e) => setImage(e.target.value)} required />
					</Form.Group>
					<Form.Group className="mb-3" controlId="category">
						<Form.Label>Categorie</Form.Label>
						<Form.Control value={category} onChange={(e) => setCategory(e.target.value)} required />
					</Form.Group>
					<Form.Group className="mb-3" controlId="openSeats">
						<Form.Label>Locuri Disponibile</Form.Label>
						<Form.Control value={openSeats} onChange={(e) => setOpenSeats(e.target.value)} required />
					</Form.Group>
					<Form.Group className="mb-3" controlId="description">
						<Form.Label>Descriere</Form.Label>
						<Form.Control value={description} onChange={(e) => setDescription(e.target.value)} required />
					</Form.Group>
					<div className="mb-3">
						<Button disabled={loadingUpdate} type="submit">
							Updateaza
						</Button>
						{loadingUpdate && <LoadingBox />}
					</div>
				</Form>
			)}
		</Container>
	);
}
