import axios from 'axios';
import { useContext, useEffect, useReducer, useRef, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Form from 'react-bootstrap/Form';
import Badge from 'react-bootstrap/Badge';
import Button from 'react-bootstrap/Button';
import Rating from '../components/Rating';
import { Helmet } from 'react-helmet-async';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { getError } from '../utils';
import { Store } from '../Store';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import { toast } from 'react-toastify';

const reducer = (state, action) => {
	switch (action.type) {
		case 'REFRESH_EVENT':
			return { ...state, event: action.payload };
		case 'CREATE_REQUEST':
			return { ...state, loadingCreateReview: true };
		case 'CREATE_SUCCESS':
			return { ...state, loadingCreateReview: false };
		case 'CREATE_FAIL':
			return { ...state, loadingCreateReview: false };
		case 'FETCH_REQUEST':
			return { ...state, loading: true };
		case 'FETCH_SUCCESS':
			return { ...state, event: action.payload, loading: false };
		case 'FETCH_FAIL':
			return { ...state, loading: false, error: action.payload };
		default:
			return state;
	}
};

function EventScreen() {
	let reviewsRef = useRef();

	const [ rating, setRating ] = useState(0);
	const [ comment, setComment ] = useState('');
	const [ selectedImage, setSelectedImage ] = useState('');

	const navigate = useNavigate();
	const params = useParams();
	const { slug } = params;

	const [ { loading, error, event, loadingCreateReview }, dispatch ] = useReducer(reducer, {
		event: [],
		loading: true,
		error: ''
	});
	useEffect(
		() => {
			const fetchData = async () => {
				dispatch({ type: 'FETCH_REQUEST' });
				try {
					const result = await axios.get(`/api/events/slug/${slug}`);
					dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
				} catch (err) {
					dispatch({ type: 'FETCH_FAIL', payload: getError(err) });
				}
			};
			fetchData();
		},
		[ slug ]
	);

	const { state, dispatch: ctxDispatch } = useContext(Store);
	const { cart, userInfo } = state;
	const addToCartHandler = async () => {
		const existItem = cart.cartItems.find((x) => x._id === event._id);
		const quantity = existItem ? existItem.quantity + 1 : 1;
		const { data } = await axios.get(`/api/events/${event._id}`);
		if (data.openSeats < quantity) {
			window.alert('Sorry. Event is out of stock');
			return;
		}
		ctxDispatch({
			type: 'CART_ADD_ITEM',
			payload: { ...event, quantity }
		});
		navigate('/cart');
	};

	const submitHandler = async (e) => {
		e.preventDefault();
		if (!comment || !rating) {
			toast.error('Please enter comment and rating');
			return;
		}
		try {
			const { data } = await axios.post(
				`/api/events/${event._id}/reviews`,
				{ rating, comment, name: userInfo.name },
				{
					headers: { Authorization: `Bearer ${userInfo.token}` }
				}
			);

			dispatch({
				type: 'CREATE_SUCCESS'
			});
			toast.success('Review submitted successfully');
			event.reviews.unshift(data.review);
			event.numReviews = data.numReviews;
			event.rating = data.rating;
			dispatch({ type: 'REFRESH_EVENT', payload: event });
			window.scrollTo({
				behavior: 'smooth',
				top: reviewsRef.current.offsetTop
			});
		} catch (error) {
			toast.error(getError(error));
			dispatch({ type: 'CREATE_FAIL' });
		}
	};
	return loading ? (
		<LoadingBox />
	) : error ? (
		<MessageBox variant="danger">{error}</MessageBox>
	) : (
		<div>
			<Row>
				<Col md={6}>
					<img className="img-large" src={selectedImage || event.image} alt={event.name} />
				</Col>
				<Col md={3}>
					<ListGroup variant="flush">
						<ListGroup.Item>
							<Helmet>
								<title>{event.name}</title>
							</Helmet>
							<h1>{event.name}</h1>
						</ListGroup.Item>
						<ListGroup.Item>
							<Rating rating={event.rating} numReviews={event.numReviews} />
						</ListGroup.Item>
						<ListGroup.Item>Pirce : ${event.price}</ListGroup.Item>
						<ListGroup.Item>
							<Row xs={1} md={2} className="g-2">
								{[ event.image, ...event.images ].map((x) => (
									<Col key={x}>
										<Card>
											<Button
												className="thumbnail"
												type="button"
												variant="light"
												onClick={() => setSelectedImage(x)}
											>
												<Card.Img variant="top" src={x} alt="event" />
											</Button>
										</Card>
									</Col>
								))}
							</Row>
						</ListGroup.Item>
						<ListGroup.Item>
							Description:
							<p>{event.description}</p>
						</ListGroup.Item>
					</ListGroup>
				</Col>
				<Col md={3}>
					<Card>
						<Card.Body>
							<ListGroup variant="flush">
								<ListGroup.Item>
									<Row>
										<Col>Price:</Col>
										<Col>${event.price}</Col>
									</Row>
								</ListGroup.Item>
								<ListGroup.Item>
									<Row>
										<Col>Status:</Col>
										<Col>
											{event.openSeats > 0 ? (
												<Badge bg="success">In Stock</Badge>
											) : (
												<Badge bg="danger">Unavailable</Badge>
											)}
										</Col>
									</Row>
								</ListGroup.Item>

								{event.openSeats > 0 && (
									<ListGroup.Item>
										<div className="d-grid">
											<Button onClick={addToCartHandler} variant="primary">
												Add to Cart
											</Button>
										</div>
									</ListGroup.Item>
								)}
							</ListGroup>
						</Card.Body>
					</Card>
				</Col>
			</Row>
			<div className="my-3">
				<h2 ref={reviewsRef}>Reviews</h2>
				<div className="mb-3">{event.reviews.length === 0 && <MessageBox>There is no review</MessageBox>}</div>
				<ListGroup>
					{event.reviews.map((review) => (
						<ListGroup.Item key={review._id}>
							<strong>{review.name}</strong>
							<Rating rating={review.rating} caption=" " />
							<p>{review.createdAt.substring(0, 10)}</p>
							<p>{review.comment}</p>
						</ListGroup.Item>
					))}
				</ListGroup>
				<div className="my-3">
					{userInfo ? (
						<form onSubmit={submitHandler}>
							<h2>Write a customer review</h2>
							<Form.Group className="mb-3" controlId="rating">
								<Form.Label>Rating</Form.Label>
								<Form.Select
									aria-label="Rating"
									value={rating}
									onChange={(e) => setRating(e.target.value)}
								>
									<option value="">Select...</option>
									<option value="1">1- Poor</option>
									<option value="2">2- Fair</option>
									<option value="3">3- Good</option>
									<option value="4">4- Very good</option>
									<option value="5">5- Excelent</option>
								</Form.Select>
							</Form.Group>
							<FloatingLabel controlId="floatingTextarea" label="Comments" className="mb-3">
								<Form.Control
									as="textarea"
									placeholder="Leave a comment here"
									value={comment}
									onChange={(e) => setComment(e.target.value)}
								/>
							</FloatingLabel>

							<div className="mb-3">
								<Button disabled={loadingCreateReview} type="submit">
									Trimite
								</Button>
								{loadingCreateReview && <LoadingBox />}
							</div>
						</form>
					) : (
						<MessageBox>
							Please <Link to={`/signin?redirect=/event/${event.slug}`}>Sign In</Link> to write a review
						</MessageBox>
					)}
				</div>
			</div>
		</div>
	);
}
export default EventScreen;
