import OrderScreen from './screens/OrderScreen';
import OrderHistoryScreen from './screens/OrderHistoryScreen';
import ProfileScreen from './screens/ProfileScreen';
import SearchBox from './components/SearchBox';
import SearchScreen from './screens/SearchScreen';
import ProtectedRoute from './components/ProtectedRoute';
import DashboardScreen from './screens/DashboardScreen';
import AdminRoute from './components/AdminRoute';
import EventListScreen from './screens/EventListScreen';
import EventEditScreen from './screens/EventEditScreen';
import BlogListScreen from './screens/BlogListScreen';
import BlogEditScreen from './screens/BlogEditScreen';
import OrderListScreen from './screens/OrderListScreen';
import UserListScreen from './screens/UserListScreen';
import UserEditScreen from './screens/UserEditScreen';
import ErrorPage from './screens/Error/ErrorPage';
import Termeni from './screens/Termeni/Termeni';
import Confidentialitate from './screens/Termeni/Confidentialitate';
import Home from './screens/Home/Home';
import Footer from './components/Footer';
import ContactUs from './screens/Contact';

function App() {
	const { state, dispatch: ctxDispatch } = useContext(Store);
	const { cart, userInfo } = state;

	const signoutHandler = () => {
		ctxDispatch({ type: 'USER_SIGNOUT' });
		localStorage.removeItem('userInfo');
		localStorage.removeItem('validareComanda');
		localStorage.removeItem('paymentMethod');
		window.location.href = '/signin';
	};

	return (
		<BrowserRouter>
			<div>
				<ToastContainer position="bottom-center" limit={1} />
				<header>
					<Navbar bg="dark" variant="dark" expand="lg">
						<Container>
							<LinkContainer to="/">
								<Navbar.Brand>T.R.</Navbar.Brand>
							</LinkContainer>
							<LinkContainer to="/">
								<Navbar.Brand>Home</Navbar.Brand>
							</LinkContainer>
							<LinkContainer to="/eventsfull">
								<Navbar.Brand>Evenimente</Navbar.Brand>
							</LinkContainer>
							<LinkContainer to="/blogsfull">
								<Navbar.Brand>Blog</Navbar.Brand>
							</LinkContainer>
							<LinkContainer to="/contact">
								<Navbar.Brand>Contact</Navbar.Brand>
							</LinkContainer>
							<Navbar.Toggle aria-controls="basic-navbar-nav" />
							<Navbar.Collapse id="basic-navbar-nav">
								<SearchBox />
								<Nav className="me-auto  w-100  justify-content-end">
									<Link to="/cart" className="nav-link">
										Cos
										{cart.cartItems.length > 0 && (
											<Badge pill bg="danger">
												{cart.cartItems.reduce((a, c) => a + c.quantity, 0)}
											</Badge>
										)}
									</Link>
									{userInfo ? (
										<NavDropdown title={userInfo.name} id="basic-nav-dropdown">
											<LinkContainer to="/profile">
												<NavDropdown.Item>Profil</NavDropdown.Item>
											</LinkContainer>
											<LinkContainer to="/orderhistory">
												<NavDropdown.Item>Istoric Comenzi</NavDropdown.Item>
											</LinkContainer>
											<NavDropdown.Divider />
											<Link className="dropdown-item" to="#signout" onClick={signoutHandler}>
												Log Out
											</Link>
										</NavDropdown>
									) : (
										<Link className="nav-link" to="/signin">
											Autentificare
										</Link>
									)}
									{userInfo &&
									userInfo.isAdmin && (
										<NavDropdown title="Admin" id="admin-nav-dropdown">
											<LinkContainer to="/admin/dashboard">
												<NavDropdown.Item>Dashboard</NavDropdown.Item>
											</LinkContainer>
											<LinkContainer to="/admin/events">
												<NavDropdown.Item>Evenimente</NavDropdown.Item>
											</LinkContainer>
											<LinkContainer to="/admin/blog">
												<NavDropdown.Item>Blog</NavDropdown.Item>
											</LinkContainer>
											<LinkContainer to="/admin/orders">
												<NavDropdown.Item>Comenzi</NavDropdown.Item>
											</LinkContainer>
											<LinkContainer to="/admin/users">
												<NavDropdown.Item>Utilizatori</NavDropdown.Item>
											</LinkContainer>
										</NavDropdown>
									)}
								</Nav>
							</Navbar.Collapse>
						</Container>
					</Navbar>
				</header>
				<main>
					<Container className="mt-3">
						<Routes>
							<Route path="/contact" element={<ContactUs />} />
							<Route path="/event/:slug" element={<EventScreen />} />
							<Route path="/cart" element={<CartScreen />} />
							<Route path="/search" element={<SearchScreen />} />
							<Route path="/signin" element={<SigninScreen />} />
							<Route path="/signup" element={<SignupScreen />} />
							<Route
								path="/profile"
								element={
									<ProtectedRoute>
										<ProfileScreen />
									</ProtectedRoute>
								}
							/>
							<Route path="/placeorder" element={<PlaceOrderScreen />} />
							<Route
								path="/order/:id"
								element={
									<ProtectedRoute>
										<OrderScreen />
									</ProtectedRoute>
								}
							/>
							<Route
								path="/orderhistory"
								element={
									<ProtectedRoute>
										<OrderHistoryScreen />
									</ProtectedRoute>
								}
							/>
							<Route path="/shipping" element={<ValidareComandaScreen />} />
							<Route path="/payment" element={<PaymentMethodScreen />} />
							{/* Admin Routes */}
							<Route
								path="/admin/dashboard"
								element={
									<AdminRoute>
										<DashboardScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/orders"
								element={
									<AdminRoute>
										<OrderListScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/users"
								element={
									<AdminRoute>
										<UserListScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/events"
								element={
									<AdminRoute>
										<EventListScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/event/:id"
								element={
									<AdminRoute>
										<EventEditScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/blogs"
								element={
									<AdminRoute>
										<BlogListScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/blog/:id"
								element={
									<AdminRoute>
										<BlogEditScreen />
									</AdminRoute>
								}
							/>
							<Route
								path="/admin/user/:id"
								element={
									<AdminRoute>
										<UserEditScreen />
									</AdminRoute>
								}
							/>

							<Route path="/eventsfull" element={<Events />} />
							<Route path="/" element={<Home />} />
							<Route path="/blogsfull" element={<Blogs />} />
							<Route path="*" element={<ErrorPage />} />
						</Routes>
						<Footer />
					</Container>
				</main>
			</div>
		</BrowserRouter>
	);
}

export default App;
